public class Task {    private String name;    private boolean done;    public Task(String taskName) {        name = taskName;    }    public void setDone(boolean bool)    {        this.done = bool;    }    public boolean getDone()    {        return this.done;    }    public String getName()    {        return this.name;    }    public String doneTitle()    {        return done ? "[X] " : "[ ] ";    }}